mapper.update:
update tbl_group set g_name = #{g_name}, excercise_type = #{excercise_type}, g_attend = #{g_attend},
		g_intro = #{g_intro}, g_location = #{g_location}, g_pic = #{g_pic, jdbcType=VARCHAR}
	where gno = #{gno}

mapper.delete:
delete from tbl_group_join
where gno = #{gno}
and userid = #{userid}
