mapper.update:
update tbl_group_board set
	gb_title = #{gb_title},
	gb_content = #{gb_content},
	gb_pic = #{gb_pic, jdbcType=VARCHAR}
where gbno = #{gbno}

update tbl_group_board
set gb_comment = gb_comment + #{cnt}
where gbno = #{gbno}

update tbl_group set
g_leader = #{g_leader}
where gno = #{gno}
