mapper.select:
select * from tbl_group_board_comment
where gbno = #{gbno}
order by gbcno desc

mapper.update:
update tbl_group_board_comment set
	gbc_content = #{gbc_content}
where gbcno = #{gbcno}

update tbl_group_board
set gb_comment = gb_comment + #{cnt}
where gbno = #{gbno}

update tbl_group_board
set gb_comment = gb_comment - #{cnt}
where gbno = #{gbno}
