mapper.select:
select * from tbl_user
where userid = #{userid}
and userpw = #{userpw}

select * from tbl_user
where username = #{username}
and email = #{email}

select * from tbl_user
where userid = #{userid}
and email = #{email}

mapper.update:
update tbl_user set
	userpw = #{userpw}
where userid = #{userid}
